// Input: Agent JSON on $json.data; past themes on $json.pastThemes (array of strings)
// Output: either passes through or throws to trigger a regenerate loop

function normalize(s) {
  return s.toLowerCase().replace(/\s+/g, ' ').trim();
}

function jaroWinkler(s1, s2) {
  // Lightweight similarity; good enough for titles
  const m = Math.floor(Math.max(s1.length, s2.length) / 2) - 1;
  let matches1 = new Array(s1.length).fill(false);
  let matches2 = new Array(s2.length).fill(false);
  let matches = 0, transpositions = 0;

  for (let i = 0; i < s1.length; i++) {
    const start = Math.max(0, i - m), end = Math.min(i + m + 1, s2.length);
    for (let j = start; j < end; j++) {
      if (!matches2[j] && s1[i] === s2[j]) { matches1[i] = matches2[j] = true; matches++; break; }
    }
  }
  if (matches === 0) return 0;

  let k = 0;
  for (let i = 0; i < s1.length; i++) {
    if (matches1[i]) {
      while (!matches2[k]) k++;
      if (s1[i] !== s2[k]) transpositions++;
      k++;
    }
  }
  transpositions /= 2;

  const j = (matches / s1.length + matches / s2.length + (matches - transpositions) / matches) / 3;

  // Winkler prefix boost
  let prefix = 0;
  for (let i = 0; i < Math.min(4, s1.length, s2.length); i++) {
    if (s1[i] === s2[i]) prefix++; else break;
  }
  return j + prefix * 0.1 * (1 - j);
}

const agent = $json.output; // JSON from Agent
const theme = normalize(agent.prompt || '');
const line  = normalize(agent.title || '');

const past = ($json.pastThemes || []).map(normalize);
const candidate = `${theme} | ${line}`;

// Quick hash to catch exact/near-exact repeats
function simpleHash(s) {
  let h = 0; for (let i = 0; i < s.length; i++) h = (h * 31 + s.charCodeAt(i)) | 0;
  return h >>> 0;
}
const candHash = simpleHash(candidate);

// Thresholds: adjust to be stricter/looser
const SIM_THRESH = 0.90; // 0..1 (higher = stricter)

let tooSimilar = false;
for (const prev of past) {
  const sim = jaroWinkler(candidate, prev);
  if (sim >= SIM_THRESH || simpleHash(prev) === candHash) {
    tooSimilar = true; break;
  }
}

if (tooSimilar) {
  // Throwing makes the node fail and lets you loop back to the Agent
  throw new Error('Theme too similar to a past entry. Regenerate.');
}

// If it's unique, expose it for saving
return { ...agent, _hash: candHash };

function extractTheme(text = '') {
  const m = text.match(/\/theme\s+(.+?)(?=(?:\s\/[a-z]+)|[\r\n]|$)/i);
  if (!m) return null;
  let theme = m[1].trim();

  if ((theme.startsWith('"') && theme.endsWith('"')) ||
      (theme.startsWith("'") && theme.endsWith("'"))) {
    theme = theme.slice(1, -1).trim();
  }
  return theme.replace(/\s+/g, ' ');
}

const inItem = $input.first();
const text = inItem?.json?.message?.text ?? '';

const user_theme = extractTheme(text);

// Return one item (this satisfies N8nOutputItems)
return [
  {
    json: { user_theme }, // theme can be string or null
  },
];

